    try {
        const results = await req.getCode();
        res.send(results.repos);
    } catch (err) {
        console.log('problem with request: ' + err);
        res.status(500).send({
            message: 'problem with request',
            error: {
                status: '500',
                stack: 'problem with request'
            }
        });
    }
}

